@@ -0,0 +1,36 @@
+import React, { useState, useRef } from "react";
+import PetzyLanding from "./components/PetzyLanding";
+import BackgroundParticles from "./components/BackgroundParticles";
+import LeftSidebar from "./components/LeftSidebar";
+import MainContent from "./components/MainContent";
+import RightSidebar from "./components/RightSidebar";
+import PetModel from "./components/PetModel";
+
+function App() {
+  const [currentUsername, setCurrentUsername] = useState(null); // Changed from isAuthenticated
+  const petRef = useRef();
+
+  return (
+    <div
+      className={`font-['Space_Grotesk'] bg-gradient-to-br from-gray-900 via-indigo-900 to-gray-800 ${
+        currentUsername ? "h-screen overflow-hidden" : ""
+      }`}
+    >
+      <BackgroundParticles />
+      {!currentUsername ? (
+        <PetzyLanding setCurrentUsername={setCurrentUsername} />
+      ) : (
+        <div className="grid grid-cols-[290px_1fr_290px] h-screen gap-0">
+          <LeftSidebar username={currentUsername} />
+          <div className="relative w-full h-full">
+            <PetModel ref={petRef} className="absolute inset-0" />
+            <MainContent username={currentUsername} />
+          </div>
+          <RightSidebar petRef={petRef} username={currentUsername} />
+        </div>
+      )}
+    </div>
+  );
+}
+
+export default App;
